# === App Title ===
#st.set_page_config(page_title="Fraud Detection App", layout="centered")
st.title("💳 Credit Card Fraud Detection")
st.markdown("Please fill in transaction details to check if it's fraudulent.")

st.warning("⚠️ Avoid opening multiple tabs of this app simultaneously to prevent session crashes.")

# === Sidebar Input ===
with st.form("fraud_form"):
    st.subheader("📋 Transaction Details")

    col1, col2 = st.columns(2)
    with col1:
        merchant_input = st.selectbox("🛒 Merchant", merchant_list)
        category_input = st.selectbox("📦 Category", category_list)
    with col2:
        job_input = st.selectbox("👨‍💼 Customer Job", job_list)
        amount_input = st.slider("💰 Transaction Amount ($)", 0.0, 5000.0, 100.0, step=10.0)

    selected_model = st.selectbox("⚙️ Choose ML Model", list(models.keys()))

    submit = st.form_submit_button("🚨 Detect Fraud")

# === Prediction Triggered ===
if submit:
    try:
        # Category encoding
        merchant_code = pd.Series([merchant_input], dtype="category").cat.set_categories(merchant_list).cat.codes[0]
        category_code = pd.Series([category_input], dtype="category").cat.set_categories(category_list).cat.codes[0]
        job_code = pd.Series([job_input], dtype="category").cat.set_categories(job_list).cat.codes[0]

        # Scale transaction amount
        scaled_amt = scaler.transform([[amount_input]])[0][0]

        # Create input dict
        input_dict = {
            "merchant": merchant_code,
            "category": category_code,
            "job": job_code,
            "scaled_amt": scaled_amt
        }

        for col in features:
            if col not in input_dict:
                input_dict[col] = 0

        input_df = pd.DataFrame([input_dict])[features]

        # Make prediction
        model = models[selected_model]
        prob = model.predict_proba(input_df)[0][1]

        # Display result
        st.subheader("🔍 Prediction Result")
        st.success(f"Model: **{selected_model}**")
        st.metric("Fraud Probability", f"{prob * 100:.2f}%")

    except Exception as e:
        st.error(f"❌ Error: {e}")







